import React, { useState, useEffect } from "react";
import db from "../../firebaseConfig";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const ItemForm = ({ boardTitle, boardsItems, boardsId, itemData }) => {
  // const [items, setItems] = useState(oneBoard);
  const [items, setItems] = useState(boardsItems);
  const [userInput, setUserInput] = useState("");

  // const docId = db.collection("boards").doc().id;

  const addItem = async () => {
    // console.log(boardsId)
    await db
      .collection("boards")
      .doc(boardsId)
      .update({
        items: [...items, userInput],
      });
    setItems([...items, userInput]);
    itemData((prevState) => prevState + 1);
  };

  // useEffect(() => {}, []);

  const handleInputValue = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addItem();
    setUserInput("");
  };

  // <Form>
  //       <Form.Row>
  //         <Form.Group controlId="addBoard">
  //           <Form.Control
  //             type="text"
  //             placeholder="Enter board name"
  //             name="title"
  //             value={newBoard.title}
  //             onChange={(e) => handleInputValue(e)}
  //           />
  //         </Form.Group>
  //       </Form.Row>
  //       {/* <input></input> */}
  //       <Button
  //         variant="primary"
  //         type="submit"
  //         onClick={(e) => handleSubmit(e)}
  //       >
  //         Add Board
  //       </Button>
  //       {/* <button >Add board</button> */}
  //     </Form>

  return (
    <div>
      <Form>
        <Form.Row>
          //{" "}
          <Form.Group controlId="addBoard">
            <Form.Control
              name="items"
              onChange={(e) => handleInputValue(e)}
              value={userInput}
            />
          </Form.Group>
        </Form.Row>
        <Button onSubmit={(e) => handleSubmit(e)}>Add Item</Button>
        <button></button>
      </Form>
    </div>
  );
};

export default ItemForm;
