import React, { useState, useEffect } from "react";
import db from "../../firebaseConfig";
// import Board from "./";

const BoardsContainer = () => {
  const [boards, setBoards] = useState("");

  useEffect(() => {
    async function fetchBoardsData() {
      const boardsResponse = await db.collection("boards").docs().get();
      const boardsData = boardsResponse.data();
      console.log(boardsResponse);
      setBoards(boardsData);
    }
    fetchBoardsData();
  }, []);
  //   console.log(boards);
  return (
    <div>
      {boards.map((el) => {
        return (
          <div>
            <h3>{el.title}</h3>
            {el.items.map((item) => {
              return <p>{item}</p>;
            })}
          </div>
        );
      })}
    </div>
  );
};

export default BoardsContainer;

