import React, {Component} from "react";
import { DownOutlined,PlusOutlined } from '@ant-design/icons';
import { Select, Row, Col } from 'antd';
import sortImg from './Images/sort.png'

export default function SortBy({sortBoards}) {
    const {Option} = Select

    const handleChange = (value) => {
        sortBoards(value)
    }

    const handleClick = (e) => {

        return ( <Select defaultValue="title" onChange={handleChange} >
        <Option value="date">Date</Option>
        <Option value="title">Title</Option>
        <Option value="dDate">Date (descending)</Option>
        <Option value="dTitle">Title (descending)</Option>
    </Select>)
    }
    return (
        <>
        <Row>
            {/* <Col>
                {}
            </Col> */}
        <img src={sortImg} style={{width: '20px', height: '20px'}} onClick={e => handleClick(e)}>

        </img>
            {/* <Col>
                <Select defaultValue="title" onChange={handleChange} >
                    <Option value="date">Date</Option>
                    <Option value="title">Title</Option>
                    <Option value="dDate">Date (descending)</Option>
                    <Option value="dTitle">Title (descending)</Option>
                </Select>
            </Col> */}
        </Row>
        </>
    )
}
