import React, { useState, useEffect } from "react";
import db from "../../firebaseConfig";
import Board from "../../components/board/Board";
import BoardForm from "../../components/boardForm/BoardForm";
import "antd/dist/antd.css";
import {Card, List, Switch, Menu, Dropdown, Row, Col} from "antd"
import {byTitle, byTitleD, byDate, byDateD} from "../../functions"
import SortBy from '../../components/sortBy/SortBy'
import ListView from '../../components/listView'
import BoardViewImg from './chess-board.svg' 


const BoardsContainer = () => {
  const [boards, setBoards] = useState([]);
  const [isLoading, setIsLoading] = useState(true)
  const [sortedBy, setSortedBy] = useState("")
  const [listView, setListView] = useState(true)


  const sortBoards = (sortedBy) => {
    console.log(sortedBy)
    switch(sortedBy){
      case "2":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitle)
          return newBoard
        })
          break
      case "4":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitleD)
          return newBoard
        })
          break
      case "1":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byDate)
          return newBoard
        })
          break
      case "3":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byDateD)
          return newBoard
        })
          break
      default:
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitle)
          return newBoard
        })
    }
  }

  useEffect(() => {
    return db.collection("boards").onSnapshot((snapshot) => {
      if (snapshot.docChanges().length === 0){
        setIsLoading(false)
      }
      snapshot.docChanges().forEach((change) => {
        if (change.type === "added") {
          setBoards((prevBoards) => [
            ...prevBoards,
            { ...change.doc.data(), id: change.doc.id },
          ]);
          setIsLoading(false)
        }
        if (change.type === "modified") {
          setBoards((prevBoards) => {
            const newArrBoards = [...prevBoards];
            let index = newArrBoards.findIndex((el) => el.id === change.doc.id);
            if (index !== -1) {
              newArrBoards[index] = {
                ...change.doc.data(),
                id: change.doc.id,
              };
            }
            return newArrBoards;
          });
          setIsLoading(false)
        }
        if (change.type === "removed") {
          setBoards((prevBoards) => {
            const newArrBoards = [...prevBoards];
            let index = newArrBoards.findIndex((el) => el.id === change.doc.id);

            if (index !== -1) {
              newArrBoards.splice(index, 1);
            }
            return newArrBoards;
          });

        }
      });
    });
  }, []);


 const handleChange = () => {
    setListView(!listView)
  }

  const gridStyle = {
    margin: '15px',
    width: '30%'
  }

const dropdownView = () => {
  return (
    <Menu onClick={e => handleChange(e)}>
      <Menu.Item key="1">Board</Menu.Item>
      <Menu.Item key="2">List</Menu.Item>
    </Menu>
)
}
  const boardViewStyle = {
    width: '15px',
    color: 'white'
  }
  const sortViewStyle = {
    marginRight: '1rem',
    // marginLeft: '7px',
    marginTop: '5rem'
  }
  return (
    <div>
      <Row className="content-header">

          <Col>
            <BoardForm />
          </Col>
      <Row>
       <Col style={sortViewStyle}>
       <p>View</p>
       </Col>
       <Col style={sortViewStyle}>
       <Dropdown overlay={dropdownView} >
    <img src={BoardViewImg} style={boardViewStyle} onClick={e => e.preventDefault()} />
  </Dropdown>
       </Col>

       <Col style={sortViewStyle}>
            <SortBy sortBoards={sortBoards} />
          </Col>

       </Row>

      </Row>
        <Row>
{ listView ? 
    boards.map((el) => {
            return (
              <Card.Grid style={gridStyle}>
                <Board
                  key={el.id}
                  boardTitle={el.title}
                  boardsItems={el.items ? el.items : []}
                  boardsId={el.id}
                />
               </Card.Grid>
            )
          })
            :
           boards.map((el) => {
            return (
              <List.Item >
                <ListView
                  key={el.id}
                  boardTitle={el.title}
                  boardsItems={el.items ? el.items : []}
                  boardsId={el.id}
                />
               </List.Item>
            );
          })
        }
        </Row>

    </div>
  );
};

export default BoardsContainer;
