import React, { useState, useEffect } from "react";
import db from "../../firebaseConfig";
import Board from "../../components/board/Board";
import BoardsForm from "../../components/boardForm/BoardForm";
import { Container } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const BoardsContainer = () => {
  const [boards, setBoards] = useState([]);

  useEffect(() => {
    return db.collection("boards").onSnapshot((snapshot) => {
      // const boardData = [];
      snapshot.docChanges().forEach(
        // (doc) => boardData.push({ ...doc.data(), id: doc.id })
        (change) => {
          if (change.type === "added") {
            setBoards((prevBoards) => [
              ...prevBoards,
              { ...change.doc.data(), id: change.doc.id },
            ]);
            // boardData.push();
            // console.log(change.doc.data());
          }
          if (change.type === "modified") {
            console.log(change.doc.data(), change.doc.id);
            setBoards((prevBoards) => {
              const newArrBoards = [...prevBoards];
              let index = newArrBoards.findIndex(
                (el) => el.id === change.doc.id
              );
              console.log(index);
              if (index !== -1) {
                newArrBoards[index] = {
                  ...change.doc.data(),
                  id: change.doc.id,
                };
              }
              return newArrBoards;
            });
          }
          if (change.type === "removed") {
            setBoards((prevBoards) => {
              const newArrBoards = [...prevBoards];
              let index = newArrBoards.findIndex(
                (el) => el.id === change.doc.id
              );

              if (index !== -1) {
                newArrBoards.splice(index, 1);
                console.log(newArrBoards);
              }
              return newArrBoards;
            });
          }
        }
      );
      // setBoards([...boards, boardData]);
    });
  }, []);

  console.log(boards);
  return (
    <div>
      <Container>
        <Row>
          <Col>
            <BoardsForm />
          </Col>
        </Row>
        <Row>
          {boards.map((el) => {
            return (
              <Board
                key={el.id}
                boardTitle={el.title}
                boardsItems={el.items}
                boardsId={el.id}
              />
            );
          })}
        </Row>
      </Container>
    </div>
  );
};

export default BoardsContainer;
