import React, { useState, useEffect } from "react";
import db from "../../firebaseConfig";
import Board from "../../components/board/Board";
import BoardsForm from "../../components/boardForm/BoardForm";
import { Container } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "antd/dist/antd.css";
import {Card, List, Switch} from "antd"
// import Loading from "../../components/board/Loading"
import {byTitle, byTitleD, byDate, byDateD} from "../../functions"
import SortBy from '../../components/sortBy/SortBy'
import ListView from '../../components/listView'


const BoardsContainer = () => {
  const [boards, setBoards] = useState([]);
  const [isLoading, setIsLoading] = useState(true)
  const [sortedBy, setSortedBy] = useState("")
  const [listView, setListView] = useState(true)


  const sortBoards = (sortedBy) => {
    switch(sortedBy){
      case "title":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitle)
          return newBoard
        })
          break
      case "dTitle":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitleD)
          return newBoard
        })
          break
      case "date":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byDate)
          return newBoard
        })
          break
      case "dDate":
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byDateD)
          return newBoard
        })
          break
      default:
        setBoards((prevBoards) => {
          const newBoards = [...prevBoards];
          let newBoard = newBoards.sort(byTitle)
          return newBoard
        })
    }
  }

  useEffect(() => {
    return db.collection("boards").onSnapshot((snapshot) => {
      if (snapshot.docChanges().length === 0){
        setIsLoading(false)
      }
      snapshot.docChanges().forEach((change) => {
        if (change.type === "added") {
          setBoards((prevBoards) => [
            ...prevBoards,
            { ...change.doc.data(), id: change.doc.id },
          ]);
          setIsLoading(false)
        }
        if (change.type === "modified") {
          setBoards((prevBoards) => {
            const newArrBoards = [...prevBoards];
            let index = newArrBoards.findIndex((el) => el.id === change.doc.id);
            if (index !== -1) {
              newArrBoards[index] = {
                ...change.doc.data(),
                id: change.doc.id,
              };
            }
            return newArrBoards;
          });
          setIsLoading(false)
        }
        if (change.type === "removed") {
          setBoards((prevBoards) => {
            const newArrBoards = [...prevBoards];
            let index = newArrBoards.findIndex((el) => el.id === change.doc.id);

            if (index !== -1) {
              newArrBoards.splice(index, 1);
            }
            return newArrBoards;
          });

        }
      });
    });
  }, []);


 const handleChange = () => {
    setListView(!listView)
  }

  const gridStyle = {
    margin: '10px',
    width: '25%'
  }

  return (
    <div>
      <Row className="content-header">
        <Col>
          <Col>
            <BoardsForm />
          </Col>
          <Col>
            <Switch onChange={(e) => handleChange(e)} />
          </Col>
          <Col>
            <SortBy sortBoards={sortBoards}/>
          </Col>
        </Col>
      </Row>
        <Row>
{ listView ? 
    boards.map((el) => {
            return (
              <Card.Grid style={gridStyle}>
                <Board
                  key={el.id}
                  boardTitle={el.title}
                  boardsItems={el.items ? el.items : []}
                  boardsId={el.id}
                />
               </Card.Grid>
            )
          })
            :
           boards.map((el) => {
            return (
              <List.Item >
                <ListView
                  key={el.id}
                  boardTitle={el.title}
                  boardsItems={el.items ? el.items : []}
                  boardsId={el.id}
                />
               </List.Item>
            );
          })
        }
        </Row>

    </div>
  );
};

export default BoardsContainer;




