import React, { useState, useEffect } from "react";

import ItemForm from "../item/ItemForm";

const Board = ({ oneBoard, boardsItems }) => {
  return (
    <div>
      <h3>{oneBoard}</h3>
      {boardsItems.map((item) => {
        return (
          <div>
            <p>{item.dueDate}</p>
            <p>{item.urgency}</p>
          </div>
        );
      })}
      <ItemForm oneBoard={oneBoard} boardsItems={boardsItems} />
    </div>
  );
};

export default Board;
