import React, { useState } from "react";
import db from "../../firebaseConfig";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const BoardForm = ({ fetchBoardData }) => {
  const [newBoard, setNewBoard] = useState({
    title: "",
  });
  const docId = db.collection("boards").doc().id;

  const addBoard = async () => {
    await db.collection("boards").doc(docId).set({
      title: newBoard.title,
      items: [],
      id: docId,
    });
    fetchBoardData((prevState) => prevState + 1);
  };
  console.log(docId);
  const handleInputValue = (e) => {
    setNewBoard({ ...newBoard, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addBoard();
    setNewBoard({
      title: "",
    });
  };

  return (
    <div>
      <Form>
        <Form.Row>
          <Form.Group controlId="addBoard">
            <Form.Control
              type="text"
              placeholder="Enter board name"
              name="title"
              value={newBoard.title}
              onChange={(e) => handleInputValue(e)}
            />
          </Form.Group>
        </Form.Row>
        {/* <input></input> */}
        <Button variant="info" type="submit" onClick={(e) => handleSubmit(e)}>
          Add Board
        </Button>
        {/* <button >Add board</button> */}
      </Form>
    </div>
  );
};

export default BoardForm;
