  return (
    <>

      {/* <p>hello</p>
      <button onClick={e => handleClick(e)}>Hello</button> */}

  {completedTaskList.map((item) => {
          return <p>{item.title}</p>;
        })}
    </>
  );
};







export default CompletedTasks;
